import React, { Component } from 'react'


export default class Add extends Component {

  constructor(props) {
    super(props);
    this.models = {}
    this.types = {
      Air:  ['Helicopter', 'Plane'],
      Sea:  ['Boat', 'Submarine'],
      Land: ['Car', 'Truck']
    }
    this.statuses = ['OK', 'INJURED', 'MIA'];
    this.state = {
      name: '',
      nameError: '',
      vehicleGroup: '',
      currentVehicleGroup: [],
      vehicleType: '',
      currentModelGroup: [],
      vehicleModel: '',
      gearItem: '',
      gearItemError: '',
      gear: [],
      status: ''
    }
    this.handleInputChange = this.handleInputChange.bind(this);
    this.addGear = this.addGear.bind(this);
    this.submitForm = this.submitForm.bind(this);
  }

  componentDidMount () {
    fetch("http://localhost:3000/vehicleTypes")
    .then(response => response.json())
    .then(json => {
      json.map((object, group, model, type) => {
        if(!(this.models.hasOwnProperty(object.type))){
          this.models[object.type] = [object.model]
        } else if(this.models.hasOwnProperty(object.type) && !this.models[object.type].includes(object.model)) {
          this.models[object.type].push(object.model);
        }
     });
    });
    console.log(this.models);
  }

  handleGearItemChange = event => {
    this.setState({
      gearItem: event.target.value
    }, () => this.validateGearItem());
  }

  handleInputChange (event) {
      const target = event.target;
      const value = target.type === 'select' ? target.selected : target.value;
      const name = target.name;

      this.setState({
        [name]: value
      }, (name) => {
        if (name === 'name') {
          this.validateName();
        } else if (name === 'gearItem') {
          this.validateGearItem();
        }
      });

      if (name === 'name') {
        this.validateName();
      } else if (name === 'gearItem') {
        this.validateGearItem();
      } else if (name === 'vehicleGroup') {
        this.setState({
          currentVehicleGroup: this.types[value]
        });
      } else if (name === 'vehicleType') {
        this.setState({
          currentModelGroup: this.models[value]
        });
      }
      
  }
  submitForm (event) {
    event.preventDefault();
    fetch('http://localhost:3000/transformers', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: this.state.name,
        vehicleGroup: this.state.vehicleGroup,
        vehicleType: this.state.vehicleType,
        vehicleModel: this.state.vehicleModel,
        gear: this.state.gear,
        status: this.state.status
      })
    })
    .then(this.setState({ name: '', gearItem: ''}));
    
  }
  addGear (event) {
    event.preventDefault();
    if (this.state.gearItem.length >= 3) {
      this.setState({
        gear: [...this.state.gear, this.state.gearItem],
        gearItem: ''
      });
    }
    
    console.log(this.state.gear);
  }

  validateName = () => {
    this.setState({
      nameError: this.state.name.length >= 2 ? null : 'Name must be longer than 2 characters.'
    });
  }

  validateGearItem = () => {
    this.setState({
      gearItemError: this.state.gearItem.length >= 3 ? null : 'Gear must be longer than 3 characters.'
    });
  }

  render() {
    const vGroup = ['Air', 'Sea', 'Land'];
    return (
      <div className="add-transformer">
        <form>
            <input type="text" id="name" placeholder="Name" className="form-control" value={this.state.name} name="name" onChange={this.handleInputChange} />
            <div>{this.state.nameError}</div>
            <select name="vehicleGroup" className="form-control" onChange={this.handleInputChange}>
                <option selected disabled>Please select group</option>
                
                {vGroup.map((group, index) => (
                    <option key={index} value={group}>{group}</option>
                ))}
            </select> 
            <select name="vehicleType" className="form-control" onChange={this.handleInputChange}>
                <option selected disabled>Please select type</option>
                {this.state.currentVehicleGroup.map((type, index) => (
                    <option key={index} value={type}>{type}</option>
                ))}
            </select>
            <select name="vehicleModel" className="form-control" onChange={this.handleInputChange}>
                <option selected disabled>Please select model</option>
                {this.state.currentModelGroup.map((model, index) => (
                    <option key={index} value={model}>{model}</option>
                ))}
            </select>
              <input type="text" id="gear" placeholder="Gear" className="form-control" value={this.state.gearItem} name="gearItem" onChange={this.handleGearItemChange} />
              {!this.state.gear.length ? null : <div>Current gear: {this.state.gear.map((item, index) => (
                <span key={index}>{item} </span>
              ))}</div>}
              {this.state.gearItemError}
              <button id="submit-gear" class="btn btn-outline-primary" onClick={this.addGear}>Add gear</button>
            <select name="status" id="status" className="form-control" onChange={this.handleInputChange}>
              <option selected disabled>Please select status</option>
              {this.statuses.map((status, index) => (
                  <option key={index} value={status}>{status}</option>
              ))}
            </select>
            <input type="submit" id="submit" className="btn btn-outline-primary" onClick={this.submitForm} value="Add transformer"/>
        </form>
      </div>
    );
     
  }
}
