import React, { Component } from 'react'
import Add from './add';

export default class Transformers extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      addFormVisibility: false
    }
    this.toggleAddForm = this.toggleAddForm.bind(this);
    // this.updateTransformers = this.updateTransformers.bind(this);
  }
  componentDidMount() {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      "Accept": "application/json"
    });
      fetch('http://localhost:3000/transformers', {
        headers: myHeaders,
      })
      .then(response => response.json())
      .then(json => {
        this.setState({
          items: json,
          isLoaded: true
        })
        console.log(this.state.items);
      });
  }

  toggleAddForm () {
    if (!this.state.addFormVisibility) {
      this.setState({
        addFormVisibility: true
      });
    } else {
      this.setState({
        addFormVisibility: false
      });
    }
  }

  updateTransformers () {
    this.forceUpdate();
  }

  render() {
    const transformers = this.state.items.map(transformer => (
      <div key={ transformer.id }>
        <h3>{ transformer.name }</h3>
      </div>  
    ));
    return (
      <div>
        <h1>Transformer app</h1>
        { transformers }
        <button onClick={this.toggleAddForm}> + </button>
        <Add show={[this.state.addFormVisibility, this.updateTransformers]}/>
      </div>
    )
  }
}
