import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import Add from './add';
import Transformer from './transformer';


export default class Transformers extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      search: ''
    }
    this.getTransformers = this.getTransformers.bind(this);
    this.deleteTransformer = this.deleteTransformer.bind(this);
    this.filterItems = this.filterItems.bind(this);
  }
  componentWillMount() {
    this.getTransformers();
  }

  getTransformers () {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      "Accept": "application/json"
    });
      fetch('http://localhost:3000/transformers', {
        headers: myHeaders,
      })
      .then(response => response.json())
      .then(json => {
        this.setState({
          items: json,
          isLoaded: true
        })
        console.log(this.state.items);
      });
  }

  deleteTransformer (id) {
      fetch('http://localhost:3000/transformers/'+id, {
        method: 'DELETE',
        headers: {'Content-Type': 'application/json'} 
    }).then(res => res.text())

    const transformers = this.state.items;

    const filteredTransformers = transformers.filter(transformer => {
      return transformer.id !== id;
    });

    this.setState({
      items: filteredTransformers
    });
  }

  filterItems (event) {
    this.setState({
      search: event.target.value
    })
  }

  render() {

    // const {search, items} = this.state;
    const filteredItems = this.state.items.filter( item => {
      return item.name.toLowerCase().indexOf( this.state.search.toLowerCase() ) !== -1
    })

    return (
      <div>
        <h1>Transformer app</h1>

        <hr />
        <input type="text" placeholder="search" value={this.state.search} onChange={this.filterItems} />
        <hr />
        {
          filteredItems.map(item => {
              return <Transformer
                        key={item.id}
                        {...item}
                        deleteTransformer={this.deleteTransformer}
                      />
            })
        }        
      </div>
    )
  }
}
