import React, { Component } from 'react'
import Delete from './delete'

export default class Transformer extends Component {

  constructor(props) {
    super(props);

    this.deleteTransformerOnServer = this.deleteTransformerOnServer.bind(this);
  }

  deleteTransformerOnServer (id) {
    // fetch('http://localhost:3000/transformers/'+id, {
    //     method: 'DELETE',
    //     headers: {'Content-Type': 'application/json'}
    //     // body: JSON.stringify({id: this.props.id}) 
    // }).then(res => res.text())
    //   .then(res => console.log(res))
    this.props.deleteTransformer(this.props.id);
  }

  render() {
    const { id, deleteTransformer, name } = this.props;
    return (
      <div>
          
          <div className="transformer">
            <h3>{ this.props.name }</h3>
            <button onClick={this.deleteTransformerOnServer()}></button>
          </div>
        
        
      </div>
    );
  }

}
