import React, { Component } from 'react'
import Add from './add';
import Delete from './delete';
import Transformer from './transformer';

export default class Transformers extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      addFormVisibility: false
    }
    this.toggleAddForm = this.toggleAddForm.bind(this);
    this.getTransformers = this.getTransformers.bind(this);
    this.deleteTransformer = this.deleteTransformer.bind(this);
  }
  componentWillMount() {
    this.getTransformers();
  }

  getTransformers () {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      "Accept": "application/json"
    });
      fetch('http://localhost:3000/transformers', {
        headers: myHeaders,
      })
      .then(response => response.json())
      .then(json => {
        this.setState({
          items: json,
          isLoaded: true
        })
        console.log(this.state.items);
      });
  }
  deleteTransformer (id) {
      fetch('http://localhost:3000/transformers/'+id, {
        method: 'DELETE',
        headers: {'Content-Type': 'application/json'} 
    }).then(res => res.text())

    const transformers = this.state.items;

    const filteredTransformers = transformers.filter(transformer => {
      return transformer.id !== id;
    });

    this.setState({
      items: filteredTransformers
    });
  }

  toggleAddForm () {
    if (!this.state.addFormVisibility) {
      this.setState({
        addFormVisibility: true
      });
    } else {
      this.setState({
        addFormVisibility: false
      });
    }
  }

  render() {
    
    return (
      <div>
        <h1>Transformer app</h1>
        {
          this.state.items.map(item => {
            return (
              <Transformer
                key={item.id}
                {...item}
                deleteTransformer={this.deleteTransformer}
              />
            );
          })
        }
        <button onClick={this.toggleAddForm}> + </button>
        <Add show={this.state.addFormVisibility} />
      </div>
    )
  }
}
