import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';

import Add from './add';

export default class UpdateDetails extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id: '',
            name: '',
            vehicleGroup: '',
            vehicleType: '',
            vehicleModel: '',
            gear: '',
            status: '',
            textEntry: false
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.clearField = this.clearField.bind(this);
        
    }

    handleInputChange (event) {
        const target = event.target;
        const value = target.type === 'select' ? target.selected : target.value;
        const name = target.name;
  
        this.setState({
          [name]: value
        });
  
        console.log(value);
  
        if (name === 'vehicleGroup') {
  
          this.setState({
            currentVehicleGroup: this.types[value]
          });
  
          console.log(this.types[value]);
        }
  
        if (name === 'vehicleType') {
          console.log(value);
  
          this.setState({
            currentModelGroup: this.models[value]
          })
  
        }
  
        console.log(this.state);
    }

    componentWillMount () {
        console.log(this.props);
    }
    data = {
      name: ''
    }
    componentDidUpdate () {
      this.data.name = this.props.name
      this.setState({
        name: this.data.name
      })
      console.log(this.data.name);
    }

    clearField (event) {
      // this.setState({
      //   [event.target.name]: ''
      // })
    }

    render() {
        return (
          <form>
            UpdateDetails
            <div><label htmlFor="id">ID: </label><input type="text" name="id" value={this.props.id} /></div>
            <div><label htmlFor="name">Name: </label><input type="text" name="name" value={this.data.name} onFocus={this.clearField} onChange={this.handleInputChange} /></div>
            <p>{this.props.status}</p>
            <p>{this.props.vehicleType}</p>
          </form>
        );
    }
}
