import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';

import Add from './add';

export default class UpdateDetails extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id: '',
            name: '',
            vehicleGroup: '',
            vehicleType: '',
            vehicleModel: '',
            gear: '',
            status: '',
            textEntry: false
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.enterText = this.enterText.bind(this);
        this.clearText = this.clearText.bind(this);
        
    }

    handleInputChange (event) {
        const target = event.target;
        const value = target.type === 'select' ? target.selected : target.value;
        const name = target.name;
  
        this.setState({
          [name]: value
        });
  
        console.log(value);
  
        if (name === 'vehicleGroup') {
  
          this.setState({
            currentVehicleGroup: this.types[value]
          });
  
          console.log(this.types[value]);
        }
  
        if (name === 'vehicleType') {
          console.log(value);
  
          this.setState({
            currentModelGroup: this.models[value]
          })
  
        }
  
        console.log(this.state);
    }

    async enterText() {
        if (!this.state.textEntry) {
            await this.clearText()
            this.setState({ textEntry: true })
        }
    }

    clearText(event) {
        const target = event.target;
        const value = target.type === 'select' ? target.selected : target.value;
        const name = target.name;

        this.setState({
            [name]: ''
        })
    }

    componentWillMount () {
        console.log(this.props);
        this.setState({
            id: this.props.id,
            name: this.props.name,
            vehicleGroup: this.props.vehicleGroup,
            vehicleType: this.props.vehicleType,
            vehicleModel: this.props.vehicleModel,
            gear: this.props.gear,
            status: this.props.status
        })
    }

    render() {
        return (
          <form>
            UpdateDetails
            <div><label for="id">ID: </label><input type="text" name="id" value={this.state.id} /></div>
            <div><label for="name">Name: </label><input type="text" name="name" defaultValue={this.props.name} value={this.state.name} onChange={this.handleInputChange} onFocus={this.clearText} /></div>
            <p>{this.props.status}</p>
            <p>{this.props.vehicleType}</p>
          </form>
        );
    }
}
