import React, { Component } from 'react'

export default class Transformers extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }
  componentDidMount() {
      fetch('http://localhost:3000/')
      .then(response => response.json())
      .then(json => {
        this.setState({
          items: json,
          isLoaded: true
        })
      });
      console.log(this.state.items);
  }

  render() {
    return (
      <div>
        <h1>Transformer app</h1>
      </div>
    )
  }
}
