import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';


export default class UpdateDetails extends Component {

    constructor(props) {
        super(props);

        this.statuses = ['OK', 'INJURED', 'MIA'];

        this.models = {};

        this.state = {
            id: props.id,
            name: props.name,
            vehicleGroup: '',
            currentVehicleGroup: [],
            vehicleType: '',
            vehicleModel: '',
            gear: [],
            status: ''
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.input = React.createRef();
        this.gear.input = React.createRef();
        
    }

    handleSubmit(event) {
      alert("name was submitted: " + this.input.current.value + this.gear.input.current.value);
      event.preventDefault();
    }

    handleInputChange (event) {
      const target = event.target;
      const value = target.type === 'select' ? target.selected : target.value;
      const name = target.name;

      this.setState({
        [name]: value
      });

      console.log(value);

      if (name === 'vehicleGroup') {

        this.setState({
          currentVehicleGroup: this.types[value]
        });

        console.log(this.types[value]);
      }

      if (name === 'vehicleType') {
        console.log(value);

        this.setState({
          currentModelGroup: this.models[value]
        })

      }

      console.log(this.state);
  }

    static getDerivedStateFromProps(nextProps, prevState) {
        return { name: nextProps.name,
                 id: nextProps.id,
                 status: nextProps.status,
                 vehicleGroup: nextProps.vehicleGroup,
                 vehicleType: nextProps.vehicleType,
                 currentVehicleGroup: nextProps.types[nextProps.vehicleGroup],
                 vehicleModel: nextProps.vehicleModel,
                 gear: nextProps.gear
                };
   
    }

    componentDidUpdate(prevProps, prevState) {
      if (prevProps.name !== this.props.name) {
        this.setState({
          name: this.props.name,
          id: this.props.id,
          status: this.props.status,
          vehicleGroup: this.props.vehicleGroup,
          vehicleType: this.props.vehicleType,
          currentVehicleGroup: this.props.types[this.props.vehicleGroup],
          vehicleModel: this.props.vehicleModel,
          gear: this.props.gear
        })
      }
    }

    render() {
        const vGroup = ['Air', 'Sea', 'Land'];
        
        return (
          <div>
            UpdateDetails
            <form onSubmit={this.handleSubmit} key={this.props.id}>
            <div><label htmlFor="id">ID: </label><input type="text" name="id" value={this.state.id} readOnly/></div>
            <div><label htmlFor="name">Name: </label><input type="text" name="name" defaultValue={this.state.name || ''} ref={this.input}/></div>
            <div>
              <label htmlFor="status">Status: </label>
              <select name="status" id="status">
              {this.statuses.map((status, index) => {
                if (status === this.state.status) {
                  return <option key={index} value={status} selected>{status}</option>
                } else {
                  return <option key={index} value={status}>{status}</option>
                }
              })}
              </select>
            </div>
            <div>
              <label htmlFor="vehicleGroup">Vehicle group: </label>
              <select name="vehicleGroup" onChange={this.handleInputChange}>
                {vGroup.map((group, index) => {
                  if (group === this.state.vehicleGroup) {
                    return <option key={index} value={group} selected>{group}</option>
                  } else {
                    return <option key={index} value={group}>{group}</option>
                  }
                })}
              </select>
            </div>
            <div>
              <label htmlFor="vehicleType">Vehicle type: </label>
              <select name="vehicleType">
                {this.props.types[this.state.vehicleGroup].map((type, index) => {
                  if (type === this.state.vehicleType) {
                    return <option key={index} value={type} selected>{type}</option>
                  } else { 
                    return <option key={index} value={type}>{type}</option>
                  }
                  
                })}
              </select>
            </div>
            <div>
              <label htmlFor="vehicleModel">Vehicle model: </label>
              <select name="vehicleModel">
                {this.props.models[this.state.vehicleType].map((model, index) => {
                  if (model === this.state.vehicleModel) {
                    return <option key={index} value={model} selected>{model}</option>
                  } else {
                    return <option key={index} value={model}>{model}</option>
                  }
                })}
              </select>
            </div>
            <div>
              <label htmlFor="gear">Gear: {this.state.gear.join(', ')}</label>
              {/* <input type="text" name="gear" defaultValue={this.state.gear.join(', ') || ''} ref={this.gear.input}/> */}
            </div>
            <input type="submit">Update</input>
            </form>
          </div>
        );
    }
}
