import React, { Component } from 'react'

export default class Add extends Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      vehicleGroup: ''
    }
    this.handleChangeVehicleGroup = this.handleChangeVehicleGroup.bind(this);
  }

  handleChangeName (event) {}
  handleChangeVehicleGroup (event) {
      this.setState({
          vehicleGroup: event.target.value
      })
  }


  render() {
    return (
      <div className="add-transformer">
        <form>
            <input type="text" id="name" value={this.state.name} />
            <select onChange={this.handleChangeVehicleGroup}>
                <option value="air">Air</option>
                <option value="sea">Sea</option>
                <option value="land">Land</option>
            </select> 
            
        </form>
      </div>
    )
  }
}
