import React, { Component } from 'react'
import Delete from './delete'
import statuses from './add';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

export default class Transformer extends Component {

  constructor(props) {
    super(props);

    this.deleteTransformer = this.deleteTransformer.bind(this);
  }

  deleteTransformer () {
    this.props.deleteTransformer(this.props.id);
  }

  render() {
    const { id, deleteTransformer, name } = this.props;
    return (
      <div>
          
          <div className="transformer">
            <h3>{ this.props.name }</h3>
            <span>{ this.props.status }</span>
            <select name="status" id="status" onChange={this.handleInputChange}>
              <option selected disabled>Please select status</option>
              {this.props.statuses.map((status, index) => { 
                  if (this.props.status === status ) {
                        return <option selected key={ index } value={ status }>{ status }</option>;
                  } else {
                    return <option key={ index } value={ status }>{ status }</option>;
                  }
                })}
            </select>
          </div>
          <button onClick={this.deleteTransformer}><FontAwesomeIcon icon="trash" /></button>
        
      </div>
    );
  }

}
