import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';

import Add from './add';

export default class UpdateDetails extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id: '',
            name: '',
            vehicleGroup: '',
            vehicleType: '',
            vehicleModel: '',
            gear: '',
            status: ''
        }

        
    }

    componentDidMount () {
        console.log(this.props);
        this.setState({
            id: this.props.id,
            name: this.props.name,
            vehicleGroup: this.props.vehicleGroup,
            vehicleType: this.props.vehicleType,
            vehicleModel: this.props.vehicleModel,
            gear: this.props.gear,
            status: this.props.status
        })
    }

    render() {
        return (
          <form>
            UpdateDetails
            <div><label for="id">ID: </label><input type="text" name="id" value={this.props.id} /></div>
            <div><label for="name">ID: </label><input type="text" name="name" value={this.state.name} /></div>
            <p>{this.props.status}</p>
            <p>{this.props.vehicleType}</p>
          </form>
        );
    }
}
