import React, { Component } from 'react'
import Add from './add';
import Delete from './delete';
import Transformer from './transformer';

export default class Transformers extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      addFormVisibility: false
    }
    this.toggleAddForm = this.toggleAddForm.bind(this);
    this.getTranformers = this.getTranformers.bind(this);
  }
  componentWillMount() {
    this.getTranformers();
  }

  getTranformers () {
    const myHeaders = new Headers({
      "Content-Type": "application/json",
      "Accept": "application/json"
    });
      fetch('http://localhost:3000/transformers', {
        headers: myHeaders,
      })
      .then(response => response.json())
      .then(json => {
        this.setState({
          items: json,
          isLoaded: true
        })
        console.log(this.state.items);
      });
  }

  toggleAddForm () {
    if (!this.state.addFormVisibility) {
      this.setState({
        addFormVisibility: true
      });
    } else {
      this.setState({
        addFormVisibility: false
      });
    }
  }

  render() {
    
    return (
      <div>
        <h1>Transformer app</h1>
        {
          this.state.items.map(item => {
            <Transformer
              key={item.id}
              name={item.name}
            />
          })
        }
      </div>
    )
  }
}
