import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';


export default class UpdateDetails extends Component {

    constructor(props) {
        super(props);

        this.statuses = ['OK', 'INJURED', 'MIA'];

        this.state = {
            id: props.id,
            name: props.name,
            vehicleGroup: '',
            vehicleType: '',
            vehicleModel: '',
            gear: '',
            status: ''
        }

        this.handleSubmit = this.handleSubmit.bind(this);
        this.input = React.createRef();
        
    }

    handleSubmit(event) {
      alert("name was submitted: " + this.input.current.value);
      event.preventDefault();
    }

    static getDerivedStateFromProps(nextProps, prevState) {
      if(nextProps.name !== prevState.name) {
        return { name: nextProps.name,
                 id: nextProps.id,
                 status: nextProps.status
                };
      } else return null;
    }

    componentDidUpdate(prevProps, prevState) {
      if (prevProps.name !== this.props.name) {
        this.setState({
          name: this.props.name,
          id: this.props.id,
          status: this.props.status
        })
      }
    }
    
    

    render() {
        return (
          <div>
            UpdateDetails
            <form onSubmit={this.handleSubmit} key={this.props.id}>
            <div><label htmlFor="id">ID: </label><input type="text" name="id" value={this.state.id} readOnly/></div>
            <div><label htmlFor="name">Name: </label><input type="text" name="name" defaultValue={this.props.name || ''} ref={this.input}/></div>
            <div>
              <label htmlFor="status">Status: </label>
              <select name="status" id="status" onChange={this.handleInputChange}>
              {this.statuses.map((status, index) => {
                if (status === this.state.status) {
                  return <option key={index} value={status} selected>{status}</option>
                } else {
                  return <option key={index} value={status}>{status}</option>
                }
              })}
              </select>
            </div>
            <div>{this.props.vehicleType}</div>
            
            </form>
          </div>
        );
    }
}
