import React, { Component } from 'react';

import Add from './add';

export default class DetailsPage extends Component {

    constructor(props) {
        super(props);

        this.state = {
            items: [],
            addFormVisibility: false
        }

        this.getTransformers = this.getTransformers.bind(this);
        this.toggleAddForm = this.toggleAddForm.bind(this);
    }

    getTransformers() {
        const myHeaders = new Headers({
            "Content-Type": "application/json",
            "Accept": "application/json"
        });
        fetch('http://localhost:3000/transformers', {
            headers: myHeaders,
        })
        .then(response => response.json())
        .then(json => {
            this.setState({
                items: json
            })
            console.log(this.state.items);
            });
    }

    toggleAddForm() {
        this.setState({
            addFormVisibility: !this.state.addFormVisibility
        })
    }

    componentWillMount() {
        this.getTransformers();
    }

    render() {
        return (
          <div>
            DetailsPage
            {
                this.state.items.map(item => {
                    return (
                        <div key={item.id}>
                            <h3>{item.name}</h3>
                            <span>{item.id}</span>
                            <p>{item.vehicleGroup}</p>
                            <p>{item.vehicleType}</p>
                            <p>{item.vehicleModel}</p>
                            <p>{item.gear}</p>
                            <p>{item.status}</p>
                            <button onClick={this.toggleAddForm}>+</button>
                            <Add />
                        </div>
                    )        
                })
            }
          </div>
        );
      }
}
