import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';

import Add from './add';
import UpdateDetails from './updateDetails';


export default class DetailsPage extends Component {

    constructor(props) {
        super(props);

        this.state = {
            items: []
        }

        this.getTransformers = this.getTransformers.bind(this);
    }

    getTransformers() {
        const myHeaders = new Headers({
            "Content-Type": "application/json",
            "Accept": "application/json"
        });
        fetch('http://localhost:3000/transformers', {
            headers: myHeaders,
        })
        .then(response => response.json())
        .then(json => {
            this.setState({
                items: json
            })
            console.log(this.props.match);
            });
    }

    componentWillMount() {
        this.getTransformers();
    }

    render() {
        return (
          <div>
            DetailsPage
            {
                this.state.items.map(item => {
                    return (<div key={item.id}>
                        <Link to={`${this.props.match.url}/${item.id}`}>{item.name}</Link>
                    </div>);
                })   
            }
            <Route path={`${this.props.match.url}/:itemId`} render={(props) => <UpdateDetails {...this.state.items.find(item => item.id === this.props.match.params.itemId)}/>} />
          </div>
        );
    }
}
